from html import escape as html_escape
from typing import Union, List, Dict

from IPython.display import display, HTML
from qiskit.providers import BaseBackend

from may4_challenge.api import get_exercise_url


def show_final_answer(a1: str, a2: str, a3: str, a4: str) -> None:
    result = a1 + a2 + a3 + a4
    display(HTML(f"""
    <p style="border: 2px solid black; padding: 2rem;">
        Congratulations ðŸŽ‰! Submit the following text
        <samp
            style="font-family: monospace; background-color: #eee;"
        >{html_escape(result)}</samp>
        on the
        <a href="{html_escape(get_exercise_url(exercise_id=2, answer=result))}" target="_blank">
            IBM Quantum Challenge page
        </a>
        to see if you are correct.
    </p>
    """))


def get_counts(backend: Union[str, BaseBackend]) -> List[Dict[str, int]]:
    if str(backend) == 'ibmq_cambridge':
        return counts_cambridge
    if str(backend) == 'ibmqx2':
        return counts_x2
    if str(backend) == 'ibmq_athens':
        return counts_athens
    if str(backend) == 'ibmq_almaden':
        return counts_almaden
    if str(backend) == 'ibmq_johannesburg':
        return counts_johannesburg_sim
    if str(backend) == 'ibmq_rome':
        return counts_rome_sim
    if str(backend) == 'ibmq_rochester':
        return counts_rochester
    if str(backend) == 'ibmq_16_melbourne':
        return counts_16_melbourne
    if str(backend) == 'ibmq_singapore':
        return counts_singapore
    if str(backend) == 'ibmq_burlington':
        return counts_burlington_sim
    if str(backend) == 'ibmq_essex':
        return counts_essex_sim
    if str(backend) == 'ibmq_london':
        return counts_london
    if str(backend) == 'ibmq_valencia':
        return counts_valencia
    if str(backend) == 'ibmq_ourense':
        return counts_ourense_sim
    if str(backend) == 'ibmq_vigo':
        return counts_vigo
    if str(backend) == 'ibmq_paris':
        return counts_paris

    raise ValueError('invalid backend')


